import _ from 'lodash';
import {
    FETCH_HOMES,
   CREATE_HOME,
} from '../actions/types';

export default (state = {}, action) => {
    switch (action.type) {
        case FETCH_HOMES:
            return { ...state, ..._.mapKeys(action.payload, '_id') };
        // case FETCH_STREAM:
        //     return { ...state, [action.payload.id]: action.payload };
        case CREATE_HOME:
            return { ...state, [action.payload.id]: action.payload };
        // case EDIT_STREAM:
        //     return { ...state, [action.payload.id]: action.payload };
        // case DELETE_STREAM:
        //     return _.omit(state, action.payload);
        default:
            return state;
    }
};
