import React, {Component} from 'react';

import {connect} from "react-redux";
import Loading from "../utils/Loading";


class SelectedTask extends Component {


    render() {
        if (this.props.selectedTask === null) {
            return <div><Loading/></div>
        }
        return (

            <div>
                Selected task
                <h1>{this.props.selectedTask.name}</h1>
                <p>{this.props.selectedTask.description}</p>
                <p>{this.props.selectedTask.points}</p>
                <p>{this.props.selectedTask.isFinished ? <span>true</span> : <span>false</span>}</p>
                <p>{this.props.selectedTask.assignedUser}</p>
            </div>
        );
    }
}

const mapStateToProps = state => {
    return {selectedTask: state.selectedTask};
};

export default connect(mapStateToProps)(SelectedTask);
