import React, { Component } from 'react';

import { connect } from "react-redux";
import Loading from "../utils/Loading";


class SelectedTask extends Component {


    render() {
        if (this.props.selectedTask === null) {
            return <div><Loading /></div>
        }
        return (

            <div className="ui centered grid ">
                <div className="center aligned sixteen wide column">
                    <h2>{this.props.selectedTask.name}</h2>
                </div>
                <div className="center aligned sixteen wide column">
                    <p>Task description: {this.props.selectedTask.description}</p>
                </div>
                <div className="center aligned sixteen wide column">
                    <p>Task points: {this.props.selectedTask.points}</p>
                </div>
                <div className="center aligned sixteen wide column">
                    <p>Is this task finished: {this.props.selectedTask.isFinished ? <span>yes</span> : <span>no</span>}</p>
                </div>
                <div className="center aligned sixteen wide column">
                    <p>{this.props.selectedTask.assignedUser}Task assigned user: {this.props.selectedTask.assignedUser}</p>
                </div>

            </div>
        );
    }
}

const mapStateToProps = state => {
    return { selectedTask: state.selectedTask };
};

export default connect(mapStateToProps)(SelectedTask);
